mapper.select:
SELECT * FROM ( SELECT A.POST_ID, A.TITLE, A.CONTENT, concat(left(fnStripTags(A.CONTENT),500), if(length(fnStripTags(A.CONTENT))>500,"...","")) PLAIN_CONTENT, A.WRITER_ID, B.DEPARTMENT, concat(fnGetCodeNm(B.GRADE), " " , B.NAME ) name, b.PHOTO PHOTO, A.HIT_CNT, A.LIKE_CNT, A.REG_DATE, A.MOD_DATE, A.THUMBNAIL, ( SELECT COUNT(1) FROM POST_COMMENT D WHERE D.POST_ID = A.POST_ID) COMMENT_CNT FROM POST A INNER JOIN USERS B ON A.WRITER_ID = B.EMAIL ) T WHERE 1 = 1
AND POST_ID IN ( SELECT POST_ID FROM POST_TAG WHERE TAG_NAME IN
#{tagItem}
)
AND TITLE LIKE CONCAT('%',#{searchKeyword},'%') OR PLAIN_CONTENT LIKE CONCAT('%',#{searchKeyword},'%') OR POST_ID IN ( SELECT POST_ID FROM POST_TAG WHERE TAG_NAME = #{searchKeyword} )
ORDER BY HIT_CNT+(LIKE_CNT*2) DESC

mapper.update:
UPDATE POST SET LIKE_CNT = (
		 SELECT COUNT(1) 
		 FROM POST_LIKE
		 WHERE POST_ID = #{postId}
  )
WHERE POST_ID = #{postId}

mapper.insert:
INSERT INTO POST_LIKE(
	USER_EMAIL,
	POST_ID
) VALUES ( 
	#{writerId},
	#{postId}
)

mapper.select:
SELECT COUNT(1)
FROM POST_LIKE
WHERE USER_EMAIL = #{writerId}
AND   POST_ID    = #{postId}

mapper.delete:
DELETE FROM POST_LIKE
WHERE USER_EMAIL = #{writerId}
AND   POST_ID    = #{postId}
