mapper.select:
SELECT * FROM ( SELECT A.QNA_ID, A.TITLE, A.CONTENT, concat(left(fnStripTags(A.CONTENT),500), if(length(fnStripTags(A.CONTENT))>500,"...","")) PLAIN_CONTENT, A.WRITER_ID, B.DEPARTMENT, concat(fnGetCodeNm(B.GRADE), " " , B.NAME ) name, b.PHOTO PHOTO, A.HIT_CNT, A.LIKE_CNT, A.BET_POINT, A.REG_DATE, A.MOD_DATE, ( SELECT COUNT(1) FROM QNA_COMMENT D WHERE D.QNA_ID = A.QNA_ID) COMMENT_CNT FROM QNA A INNER JOIN USERS B ON A.WRITER_ID = B.EMAIL ) T WHERE 1 = 1
AND QNA_ID IN ( SELECT QNA_ID FROM QNA_TAG WHERE TAG_NAME IN
#{tagItem}
)
AND TITLE LIKE CONCAT('%',#{searchKeyword},'%') OR PLAIN_CONTENT LIKE CONCAT('%',#{searchKeyword},'%') OR QNA_ID IN ( SELECT QNA_ID FROM QNA_TAG WHERE TAG_NAME = #{searchKeyword} )
ORDER BY HIT_CNT+(LIKE_CNT*2) DESC

mapper.update:
UPDATE QNA SET LIKE_CNT = (
		 SELECT COUNT(1) 
		 FROM QNA_LIKE
		 WHERE QNA_ID = #{qnaId}
  )
WHERE QNA_ID = #{qnaId}

mapper.insert:
INSERT INTO QNA_LIKE(
	USER_EMAIL,
	QNA_ID
) VALUES ( 
	#{writerId},
	#{qnaId}
)

mapper.select:
SELECT COUNT(1)
FROM QNA_LIKE
WHERE USER_EMAIL = #{writerId}
AND   QNA_ID    = #{qnaId}

mapper.delete:
DELETE FROM QNA_LIKE
WHERE USER_EMAIL = #{writerId}
AND   QNA_ID    = #{qnaId}
